SELECT
count(c.city)
FROM borrow_information a LEFT JOIN recordsubmit_table c on(a.id = c.submit) 
left JOIN apply_for_loan_information b on(c.submit = b.state)  
  WHERE 
  c.state= #{state}
  and c.city=#{city}
and a.name like CONCAT('%', #{name}, '%')
and a.mobile_phone like CONCAT('%', #{mobile_phone}, '%')
and a.id_number like CONCAT('%', #{id_number}, '%')
and a.applicationnumber like CONCAT('%',#{applicationnumber}, '%')
and a.ctime BETWEEN #{ctime} and #{ctime2}
and b.amount >= #{amount} and b.amount <= #{amount2}
 ORDER BY c.submit 

	limit 0,#{limit}

